package net.yozo.services.common;import net.yozo.core.dao.QueryModel;import java.io.Serializable;/** * 柚点充值项目 *  * @author *  */public class Youdian extends QueryModel implements Serializable {	private static final long serialVersionUID = 1L;	private String id;	private String item;	private Integer quantity;	private String price;	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getItem() {		return item;	}	public void setItem(String item) {		this.item = item;	}	public Integer getQuantity() {		return quantity;	}	public void setQuantity(Integer quantity) {		this.quantity = quantity;	}	public String getPrice() {		return price;	}	public void setPrice(String price) {		this.price = price;	}}