package net.yozo.services.front.bankAccount.dao.impl;import net.yozo.core.dao.BaseDao;import net.yozo.core.dao.page.PagerModel;import net.yozo.services.front.bankAccount.bean.BankAccount;import net.yozo.services.front.bankAccount.dao.BankAccountDao;import org.springframework.stereotype.Repository;import javax.annotation.Resource;import java.util.List;@Repository("bankAccountDaoFront")public class BankAccountDaoImpl implements BankAccountDao {    @Resource	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(BankAccount e) {		return dao.selectPageList("front.bankAccount.selectPageList",				"front.bankAccount.selectPageCount", e);	}	public List selectList(BankAccount e) {		return dao.selectList("front.bankAccount.selectList", e);	}	public BankAccount selectOne(BankAccount e) {		return (BankAccount) dao.selectOne("front.bankAccount.selectOne", e);	}	public int delete(BankAccount e) {		return dao.delete("front.bankAccount.delete", e);	}	public int update(BankAccount e) {		return dao.update("front.bankAccount.update", e);	}	public int deletes(String[] ids) {		BankAccount e = new BankAccount();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(BankAccount e) {		return dao.insert("front.bankAccount.insert", e);	}	public int deleteById(int id) {		return dao.delete("front.bankAccount.deleteById", id);	}	@Override	public BankAccount selectById(String id) {		return (BankAccount) dao.selectOne("front.bankAccount.selectById", id);	}}