package net.yozo.services.front.catalog;import net.yozo.core.Services;import net.yozo.services.front.catalog.bean.Catalog;import java.util.List;public interface CatalogService extends Services<Catalog> {	/**	 * 加载指定节点下的全部节点。 如果指定的节点为NULL则加载整棵树，否则加载指定对象下的所有节点	 * 	 * @param e	 * @return	 */	public List<Catalog> loadRoot(Catalog e);	/**	 * 根据code中包含的关键字超找Catalog的id	* @param code code中包含的关键字	* */	public String selectCatalogIDbyCode(String code);}