package net.yozo.services.front.customizeOrder.impl;import net.yozo.core.ServersManager;import net.yozo.core.dao.page.PagerModel;import net.yozo.services.front.customizeOrder.CustomizeOrderService;import net.yozo.services.front.customizeOrder.bean.CustomizeOrder;import net.yozo.services.front.customizeOrder.dao.CustomizeOrderDao;import org.springframework.stereotype.Service;import javax.annotation.Resource;import java.util.List;@Service("customizeOrderServiceFront")public class CustomizeOrderServiceImpl extends ServersManager<CustomizeOrder, CustomizeOrderDao> implements        CustomizeOrderService {    @Resource(name = "customizeOrderDaoFront")    @Override    public void setDao(CustomizeOrderDao customizeOrderDao) {        this.dao = customizeOrderDao;    }    @Override    public int uploadEdit(CustomizeOrder e){        return dao.uploadEdit(e);    }    @Override    public PagerModel designerPageList(CustomizeOrder e){        return dao.designerPageList(e);    }    @Override    public PagerModel userOrders(CustomizeOrder e) {        return dao.userOrders(e);    }    @Override    public Integer designerPageListCount(String creatorID) {        return dao.designerPageListCount(creatorID);    }    @Override    public Integer userOrdersCount(String customer) {        return dao.userOrdersCount(customer);    }    @Override    public Integer designerOrderCount(Integer creator){        return dao.designerOrderCount(creator);    }}