package net.yozo.services.front.designer.dao.impl;import net.yozo.core.dao.BaseDao;import net.yozo.core.dao.page.PagerModel;import net.yozo.services.front.designer.bean.Designer;import net.yozo.services.front.designer.dao.DesignerDao;import org.apache.ibatis.annotations.Param;import org.springframework.stereotype.Repository;import javax.annotation.Resource;import java.util.List;@Repository("designerDaoFront")public class DesignerDaoImpl implements DesignerDao {    @Resource	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(Designer e) {		return dao.selectPageList("front.designer.selectPageList",				"front.designer.selectPageCount", e);	}	public List selectList(Designer e) {		return dao.selectList("front.designer.selectList", e);	}	public Designer selectOne(Designer e) {		return (Designer) dao.selectOne("front.designer.selectOne", e);	}	public int delete(Designer e) {		return dao.delete("front.designer.delete", e);	}	public int update(Designer e) {		return dao.update("front.designer.update", e);	}	public int deletes(String[] ids) {		Designer e = new Designer();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(Designer e) {		return dao.insert("front.designer.insert", e);	}	public int deleteById(int id) {		return dao.delete("front.designer.deleteById", id);	}	@Override	public Designer selectById(String id) {		return (Designer) dao.selectOne("front.designer.selectOne", id);	}	@Override	public List<Designer> selectIndexDesigner(Designer e){return dao.selectList("front.designer.selectIndexDesigner", e);}	@Override	public Designer vkstest(Designer e) {		return (Designer)dao.selectOne("front.designer.vkstest", e);	}	@Override	public Designer selectByAccountId(Integer accountId){return (Designer)dao.selectOne("front.designer.selectByAccountId", accountId);}	@Override	public int selectCount(Designer e) {		return dao.getCount("front.designer.selectCount", e);	}	@Override	public int updateInfo(Designer e){		return dao.update("front.designer.updateInfo", e);	}}