package net.yozo.services.front.designerRecommended.dao.impl;import net.yozo.core.dao.BaseDao;import net.yozo.core.dao.page.PagerModel;import net.yozo.services.front.designerRecommended.bean.DesignerRecommended;import net.yozo.services.front.designerRecommended.dao.DesignerRecommendedDao;import org.springframework.stereotype.Repository;import javax.annotation.Resource;import java.util.List;@Repository("designerRecommendedDaoFront")public class DesignerRecommendedDaoImpl implements DesignerRecommendedDao {    @Resource	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(DesignerRecommended e) {		return dao.selectPageList("front.designerRecommended.selectPageList",				"front.designerRecommended.selectPageCount", e);	}	public List selectList(DesignerRecommended e) {		return dao.selectList("front.designerRecommended.selectList", e);	}	public DesignerRecommended selectOne(DesignerRecommended e) {		return (DesignerRecommended) dao.selectOne("front.designerRecommended.selectOne", e);	}	public int delete(DesignerRecommended e) {		return dao.delete("front.designerRecommended.delete", e);	}	public int update(DesignerRecommended e) {		return dao.update("front.designerRecommended.update", e);	}	public int deletes(String[] ids) {		DesignerRecommended e = new DesignerRecommended();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(DesignerRecommended e) {		return dao.insert("front.designerRecommended.insert", e);	}	public int deleteById(int id) {		return dao.delete("front.designerRecommended.deleteById", id);	}	@Override	public DesignerRecommended selectById(String id) {		return (DesignerRecommended) dao.selectOne("front.designerRecommended.selectOne", id);	}}