package net.yozo.services.front.download.impl;import net.yozo.core.ServersManager;import net.yozo.services.front.download.DownloadService;import net.yozo.services.front.download.bean.Download;import net.yozo.services.front.download.dao.DownloadDao;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("downloadServiceFront")public class DownloadServiceImpl extends ServersManager<Download, DownloadDao> implements        DownloadService {    @Resource(name = "downloadDaoFront")    @Override    public void setDao(DownloadDao downloadDao) {        this.dao = downloadDao;    }    @Override	public int selectCount(Download download) {		return dao.selectCount(download);	}    @Override    public void updateLastDownloadTime(Download download) {        dao.updateLastDownloadTime(download);    }}