package net.yozo.services.front.enchashmentRecord.dao.impl;import net.yozo.core.dao.BaseDao;import net.yozo.core.dao.page.PagerModel;import net.yozo.services.front.enchashmentRecord.bean.EnchashmentRecord;import net.yozo.services.front.enchashmentRecord.dao.EnchashmentRecordDao;import org.springframework.stereotype.Repository;import javax.annotation.Resource;import java.math.BigDecimal;import java.util.List;@Repository("enchashmentRecordDaoFront")public class EnchashmentRecordDaoImpl implements EnchashmentRecordDao {    @Resource	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(EnchashmentRecord e) {		return dao.selectPageList("front.enchashmentRecord.selectPageList",				"front.enchashmentRecord.selectPageCount", e);	}	public List selectList(EnchashmentRecord e) {		return dao.selectList("front.enchashmentRecord.selectList", e);	}	public EnchashmentRecord selectOne(EnchashmentRecord e) {		return (EnchashmentRecord) dao.selectOne("front.enchashmentRecord.selectOne", e);	}	public int delete(EnchashmentRecord e) {		return dao.delete("front.enchashmentRecord.delete", e);	}	public int update(EnchashmentRecord e) {		return dao.update("front.enchashmentRecord.update", e);	}	public int deletes(String[] ids) {		EnchashmentRecord e = new EnchashmentRecord();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(EnchashmentRecord e) {		return dao.insert("front.enchashmentRecord.insert", e);	}	public int deleteById(int id) {		return dao.delete("front.enchashmentRecord.deleteById", id);	}	@Override	public EnchashmentRecord selectById(String id) {		return (EnchashmentRecord) dao.selectOne("front.enchashmentRecord.selectById", id);	}	@Override	public BigDecimal selectAllEnchashmentByAccount(Integer accountId){		return (BigDecimal)dao.selectOne("front.enchashmentRecord.selectAllEnchashmentByAccount", accountId);	}	public int selectPageCount(Integer account){		return (Integer)dao.selectOne("front.enchashmentRecord.selectPageCount",account);	}	}