package net.yozo.services.front.label_link.dao.impl;import net.yozo.core.dao.BaseDao;import net.yozo.core.dao.page.PagerModel;import net.yozo.services.front.label_link.bean.Label_link;import net.yozo.services.front.label_link.dao.Label_linkDao;import org.springframework.stereotype.Repository;import javax.annotation.Resource;import java.util.List;@Repository("label_linkDaoManage")public class Label_linkDaoImpl implements Label_linkDao {    @Resource	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(Label_link e) {		return dao.selectPageList("front.label_link.selectPageList",				"front.label_link.selectPageCount", e);	}	public List selectList(Label_link e) {		return dao.selectList("front.label_link.selectList", e);	}	public Label_link selectOne(Label_link e) {		return (Label_link) dao.selectOne("front.label_link.selectOne", e);	}	public int delete(Label_link e) {		return dao.delete("front.label_link.delete", e);	}	public int update(Label_link e) {		return dao.update("front.label_link.update", e);	}	public int deletes(String[] ids) {		Label_link e = new Label_link();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(Label_link e) {		return dao.insert("front.label_link.insert", e);	}	public int deleteById(int id) {		return dao.delete("front.label_link.deleteById", id);	}	@Override	public int deleteByCondition(Label_link e) {		return dao.delete("front.label_link.deleteByCondition", e);	}	public Label_link selectById(String id) {		return (Label_link) dao.selectOne(id);	}	@Override	public void deleteByLabelId(int l_id) {		dao.delete("front.label_link.deleteByLabelId", l_id);	}}