package net.yozo.services.front.orderpay.dao.impl;import net.yozo.core.dao.BaseDao;import net.yozo.core.dao.page.PagerModel;import net.yozo.services.front.order.bean.Order;import net.yozo.services.front.orderpay.bean.Orderpay;import net.yozo.services.front.orderpay.dao.OrderpayDao;import org.springframework.stereotype.Repository;import javax.annotation.Resource;import java.util.List;@Repositorypublic class OrderpayDaoImpl implements OrderpayDao {    @Resource	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(Orderpay e) {		return dao.selectPageList("front.orderpay.selectPageList",				"front.orderpay.selectPageCount", e);	}	public List selectList(Orderpay e) {		return dao.selectList("front.orderpay.selectList", e);	}	public Orderpay selectOne(Orderpay e) {		return (Orderpay) dao.selectOne("front.orderpay.selectOne", e);	}	public Orderpay selectOneByOrderId(Orderpay e) {		return (Orderpay) dao.selectOne("front.orderpay.selectByOrderId", e);	}	public int delete(Orderpay e) {		return dao.delete("front.orderpay.delete", e);	}	public int update(Orderpay e) {		return dao.update("front.orderpay.update", e);	}	public int deletes(String[] ids) {		Orderpay e = new Orderpay();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(Orderpay e) {		return dao.insert("front.orderpay.insert", e);	}	public int deleteById(int id) {		return dao.delete("front.orderpay.deleteById", id);	}	@Override	public Orderpay selectById(String id) {		return (Orderpay) dao.selectOne("front.orderpay.selectById", id);	}	@Override	public Orderpay selectByOrderId(Order exist) {		return (Orderpay) dao.selectOne("front.orderpay.selectByOrderId", exist);	}}