package net.yozo.services.front.orderpay.impl;import net.yozo.core.ServersManager;import net.yozo.services.front.order.bean.Order;import net.yozo.services.front.orderpay.OrderpayService;import net.yozo.services.front.orderpay.bean.Orderpay;import net.yozo.services.front.orderpay.dao.OrderpayDao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class OrderpayServiceImpl extends ServersManager<Orderpay, OrderpayDao> implements		OrderpayService {    @Autowired    @Override    public void setDao(OrderpayDao orderpayDao) {        this.dao = orderpayDao;    }    @Override    public Orderpay selectOneByOrderId(Order exist) {        return dao.selectByOrderId(exist);    }}