package net.yozo.services.front.pay.impl;import net.yozo.core.ServersManager;import net.yozo.services.front.order.bean.Order;import net.yozo.services.front.pay.PayService;import net.yozo.services.front.pay.bean.Pay;import net.yozo.services.front.pay.dao.PayDao;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;@Servicepublic class PayServiceImpl extends ServersManager<Pay, PayDao> implements PayService {    @Autowired    private PayDao payDao;    @Override    public void setDao(PayDao payDao) {        this.dao = payDao;    }    @Override    @Transactional    public String getWeixinQR(Order order) {        return payDao.getWeixinQR(order);    }    @Override    @Transactional    public String getZhifubaoQR(Order order) {        return payDao.getZhifubaoQR(order);    }    @Override    @Transactional    public String payQuery(Order order) {        return payDao.payQuery(order);    }}