package net.yozo.services.front.recommendTemplate.dao.impl;import net.yozo.core.dao.BaseDao;import net.yozo.core.dao.page.PagerModel;import net.yozo.services.front.recommendTemplate.bean.PushTemplate;import net.yozo.services.front.recommendTemplate.dao.PushTemplateDao;import org.springframework.stereotype.Repository;import javax.annotation.Resource;import java.util.List;@Repository("pushtemplateDaoFront")public class PushTemplateDaoImpl implements PushTemplateDao {    @Resource	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(PushTemplate e) {		return dao.selectPageList("front.recommendTemplate.selectPageList",				"front.recommendTemplate.selectPageCount", e);	}	public List selectList(PushTemplate e) {		e.setPagerSize(4);		return dao.selectList("front.recommendTemplate.selectList", e);	}	public PushTemplate selectOne(PushTemplate e) {		return (PushTemplate) dao.selectOne("front.recommendTemplate.selectOne", e);	}	public int delete(PushTemplate e) {		return dao.delete("front.recommendTemplate.delete", e);	}	public int update(PushTemplate e) {		return dao.update("front.recommendTemplate.update", e);	}	public int deletes(String[] ids) {		PushTemplate e = new PushTemplate();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(PushTemplate e) {		return dao.insert("front.recommendTemplate.insert", e);	}	public int deleteById(int id) {		return dao.delete("front.recommendTemplate.deleteById", id);	}	@Override	public PushTemplate selectById(String id) {		return null;	}}