package net.yozo.services.front.scoreDetail.bean;import net.yozo.services.front.order.bean.Order;import net.yozo.services.front.scoreTask.bean.ScoreTask;import java.io.Serializable;public class ScoreDetail extends net.yozo.services.common.ScoreDetail implements Serializable {	private static final long serialVersionUID = 1L;	private Order order;	private ScoreTask scoreTask;	public void clear() {		super.clear();		if(order!=null){			order.clear();		}		if(scoreTask!=null){			scoreTask.clear();		}	}	public Order getOrder(){		return  order;	}	public void setOrder(Order order) {		this.order = order;	}	public ScoreTask getScoreTask() {		return scoreTask;	}	public void setScoreTask(ScoreTask scoreTask) {		this.scoreTask = scoreTask;	}}