package net.yozo.services.front.scoreDetail.dao.impl;import net.yozo.core.dao.BaseDao;import net.yozo.core.dao.page.PagerModel;import net.yozo.services.front.scoreDetail.bean.ScoreDetail;import net.yozo.services.front.scoreDetail.dao.ScoreDetailDao;import org.springframework.stereotype.Repository;import javax.annotation.Resource;import java.util.List;@Repository("scoreDetailDaoFront")public class ScoreDetailDaoImpl implements ScoreDetailDao {    @Resource	private BaseDao dao;	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(ScoreDetail e) {		return dao.selectPageList("front.scoreDetail.selectPageList",				"front.scoreDetail.selectPageCount", e);	}	public List selectList(ScoreDetail e) {		return dao.selectList("front.scoreDetail.selectList", e);	}	public ScoreDetail selectOne(ScoreDetail e) {		return (ScoreDetail) dao.selectOne("front.scoreDetail.selectOne", e);	}	public int delete(ScoreDetail e) {		return dao.delete("front.scoreDetail.delete", e);	}	public int update(ScoreDetail e) {		return dao.update("front.scoreDetail.update", e);	}	public int deletes(String[] ids) {		ScoreDetail e = new ScoreDetail();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(ScoreDetail e) {		return dao.insert("front.scoreDetail.insert", e);	}	public int deleteById(int id) {		return dao.delete("front.scoreDetail.deleteById", id);	}	@Override	public ScoreDetail selectById(String id) {		return (ScoreDetail) dao.selectOne("front.scoreDetail.selectOne", id);	}}