package net.yozo.services.front.scoreTask.bean;import java.io.Serializable;public class ScoreTask extends net.yozo.services.common.ScoreTask implements		Serializable {	private static final long serialVersionUID = 1L;	private int status;	private int completionTimes;	public void clear() {		super.clear();		status=0;		completionTimes=0;	}	public int getStatus() {		return status;	}	public void setStatus(int status) {		this.status = status;	}	public int getCompletionTimes() {		return completionTimes;	}	public void setCompletionTimes(int completionTimes) {		this.completionTimes = completionTimes;	}}