package net.yozo.services.front.scoreTask.impl;import net.yozo.core.ServersManager;import net.yozo.services.front.scoreTask.ScoreTaskService;import net.yozo.services.front.scoreTask.bean.ScoreTask;import net.yozo.services.front.scoreTask.dao.ScoreTaskDao;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Service("scoreTaskServiceFront")public class ScoreTaskServiceImpl extends ServersManager<ScoreTask, ScoreTaskDao> implements        ScoreTaskService {    @Resource(name = "scoreTaskDaoFront")    @Override    public void setDao(ScoreTaskDao scoreTaskDaoDao) {        this.dao = scoreTaskDaoDao;    }    @Override    public ScoreTask getScoreTaskByFlag(String flag) {        return dao.getScoreTaskByFlag(flag);    }}