package net.yozo.services.front.sms.dao.impl;import net.yozo.core.dao.BaseDao;import net.yozo.core.dao.page.PagerModel;import net.yozo.core.sms.SMSWebChinese;import net.yozo.services.front.sms.bean.Sms;import net.yozo.services.front.sms.dao.SmsDao;import org.springframework.stereotype.Repository;import javax.annotation.Resource;import java.io.IOException;import java.util.List;@Repository("smsDaoManage")public class SmsDaoImpl implements SmsDao {    @Resource	private BaseDao dao;	private static SMSWebChinese swc=new SMSWebChinese();	public void setDao(BaseDao dao) {		this.dao = dao;	}	public PagerModel selectPageList(Sms e) {		return dao.selectPageList("front.sms.selectPageList", "front.sms.selectPageCount",				e);	}	public List selectList(Sms e) {		return dao.selectList("front.sms.selectList", e);	}	public Sms selectOne(Sms e) {		return (Sms) dao.selectOne("front.sms.selectOne", e);	}	public int delete(Sms e) {		return dao.delete("front.sms.delete", e);	}	public int update(Sms e) {		return dao.update("front.sms.update", e);	}	public int deletes(String[] ids) {		Sms e = new Sms();		for (int i = 0; i < ids.length; i++) {			e.setId(ids[i]);			delete(e);		}		return 0;	}	public int insert(Sms e) {		return dao.insert("front.sms.insert", e);	}	public int deleteById(int id) {		return dao.delete("front.sms.deleteById", id);	}	@Override	public Sms selectById(String id) {		return (Sms) dao.selectOne("front.sms.selectById",id);	}	@Override	public String sendVerification(String phoneData, String sendContent) {		Sms sms = new Sms();		sms.setPhone(phoneData);		sms.setContent(sendContent);		sms.setSendCount(1);		try {			swc.sendSMS(sms);		} catch (IOException e) {			e.printStackTrace();		}		return sms.getReturnCode();	}}